CREATE TABLE categorias_proyectos (id BIGINT AUTO_INCREMENT, categoria VARCHAR(255) NOT NULL, slug VARCHAR(255), is_active TINYINT(1) DEFAULT '0', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, position BIGINT, UNIQUE INDEX categorias_proyectos_position_sortable_idx (position), UNIQUE INDEX categorias_proyectos_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE categorias_publicaciones (id BIGINT AUTO_INCREMENT, categoria VARCHAR(255) NOT NULL, slug VARCHAR(255), is_active TINYINT(1) DEFAULT '0', created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, position BIGINT, UNIQUE INDEX categorias_publicaciones_position_sortable_idx (position), UNIQUE INDEX categorias_publicaciones_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE clientes (id BIGINT AUTO_INCREMENT, cliente VARCHAR(255) NOT NULL, imagen VARCHAR(255) NOT NULL, is_active TINYINT(1) DEFAULT '1', position BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX clientes_position_sortable_idx (position), UNIQUE INDEX clientes_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE configuracion (id BIGINT AUTO_INCREMENT, seccion VARCHAR(255) NOT NULL, value VARCHAR(100) NOT NULL, imagen VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX configuracion_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE paginas (id BIGINT AUTO_INCREMENT, pagina VARCHAR(100) NOT NULL, contenido text NOT NULL, imagen VARCHAR(255) NOT NULL, fondo VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX paginas_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE proyectos (id BIGINT AUTO_INCREMENT, categoria_id BIGINT, titulo VARCHAR(255) NOT NULL, contenido text NOT NULL, descripcion_corta text NOT NULL, is_active TINYINT(1) DEFAULT '0', thumbnail VARCHAR(255) DEFAULT 'sin_imagen.jpg' NOT NULL, position INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX proyectos_sluggable_idx (slug), INDEX categoria_id_idx (categoria_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE proyectos_galeria (id BIGINT AUTO_INCREMENT, publicacion_id BIGINT, titulo VARCHAR(255) DEFAULT NULL, contenido text DEFAULT NULL, tipo_archivo VARCHAR(255) DEFAULT '1', archivo VARCHAR(255) NOT NULL, thumbnail VARCHAR(255) DEFAULT 'sin_imagen.jpg' NOT NULL, position INT, INDEX publicacion_id_idx (publicacion_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE publicaciones (id BIGINT AUTO_INCREMENT, categoria_id BIGINT, titulo VARCHAR(255) NOT NULL, contenido text NOT NULL, descripcion_corta text NOT NULL, is_active TINYINT(1) DEFAULT '0', thumbnail VARCHAR(255) DEFAULT 'sin_imagen.jpg' NOT NULL, position INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, slug VARCHAR(255), UNIQUE INDEX publicaciones_sluggable_idx (slug), INDEX categoria_id_idx (categoria_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE publicaciones_galeria (id BIGINT AUTO_INCREMENT, publicacion_id BIGINT, titulo VARCHAR(255) DEFAULT NULL, contenido text DEFAULT NULL, tipo_archivo VARCHAR(255) DEFAULT '1', archivo VARCHAR(255) NOT NULL, thumbnail VARCHAR(255) DEFAULT 'sin_imagen.jpg' NOT NULL, position INT, INDEX publicacion_id_idx (publicacion_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_forgot_password (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, unique_key VARCHAR(255), expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_group_permission (group_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(group_id, permission_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_permission (id BIGINT AUTO_INCREMENT, name VARCHAR(255) UNIQUE, description TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_remember_key (id BIGINT AUTO_INCREMENT, user_id BIGINT, remember_key VARCHAR(32), ip_address VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX user_id_idx (user_id), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user (id BIGINT AUTO_INCREMENT, first_name VARCHAR(255), last_name VARCHAR(255), email_address VARCHAR(255) NOT NULL UNIQUE, username VARCHAR(128) NOT NULL UNIQUE, algorithm VARCHAR(128) DEFAULT 'sha1' NOT NULL, salt VARCHAR(128), password VARCHAR(128), is_active TINYINT(1) DEFAULT '1', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX is_active_idx_idx (is_active), PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_group (user_id BIGINT, group_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, group_id)) ENGINE = INNODB;
CREATE TABLE sf_guard_user_permission (user_id BIGINT, permission_id BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(user_id, permission_id)) ENGINE = INNODB;
ALTER TABLE proyectos ADD CONSTRAINT proyectos_categoria_id_categorias_proyectos_id FOREIGN KEY (categoria_id) REFERENCES categorias_proyectos(id) ON DELETE CASCADE;
ALTER TABLE proyectos_galeria ADD CONSTRAINT proyectos_galeria_publicacion_id_proyectos_id FOREIGN KEY (publicacion_id) REFERENCES proyectos(id) ON DELETE CASCADE;
ALTER TABLE publicaciones ADD CONSTRAINT publicaciones_categoria_id_categorias_publicaciones_id FOREIGN KEY (categoria_id) REFERENCES categorias_publicaciones(id) ON DELETE CASCADE;
ALTER TABLE publicaciones_galeria ADD CONSTRAINT publicaciones_galeria_publicacion_id_publicaciones_id FOREIGN KEY (publicacion_id) REFERENCES publicaciones(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_forgot_password ADD CONSTRAINT sf_guard_forgot_password_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_group_permission ADD CONSTRAINT sf_guard_group_permission_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_remember_key ADD CONSTRAINT sf_guard_remember_key_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_group ADD CONSTRAINT sf_guard_user_group_group_id_sf_guard_group_id FOREIGN KEY (group_id) REFERENCES sf_guard_group(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_user_id_sf_guard_user_id FOREIGN KEY (user_id) REFERENCES sf_guard_user(id) ON DELETE CASCADE;
ALTER TABLE sf_guard_user_permission ADD CONSTRAINT sf_guard_user_permission_permission_id_sf_guard_permission_id FOREIGN KEY (permission_id) REFERENCES sf_guard_permission(id) ON DELETE CASCADE;
